mapper.select:
SELECT

from active_index


    project_id=#{projectId}


    AND confirm_active_id = #{activeConfirmId}


    AND is_tmp = #{isTmp}

  ORDER  by update_time desc limit 1

SELECT
id,name,active_type ,project_id,create_time
from active_index


    project_id=#{projectId}


    AND active_type = #{activeType}


    AND active_type in(1,2)


    AND is_tmp = #{isTmp}


    AND name like concat('%',#{activeName},'%')


    AND is_variant is null


order by create_time desc
limit #{pageNum},#{pageSize}

SELECT
id,name,active_type ,project_id
from active_index


    project_id=#{projectId}


    AND active_type = #{activeType}


    AND active_type in(1,2)


    AND is_tmp = #{isTmp}


    AND is_variant = #{isVariant}


    AND is_variant is null


    AND name like concat('%',#{activeName},'%')


    AND id = #{dePactiveId}


    And id not IN

      #{item}

SELECT count(*) from active_index


    project_id=#{projectId}


    AND active_type = #{activeType}


    AND active_type in(1,2)


    AND is_tmp = #{isTmp}


    AND is_variant = #{isVariant}


    AND is_variant is null


    AND name like concat('%',#{activeName},'%')

select

from active_index


    name = #{name}


    and project_id=#{projectId}


    AND is_tmp = #{isTmp}


    AND is_variant = #{isVariant}


    AND is_variant is null


    AND active_type = #{activeType}

